from typing import List
from collections import deque
class Solution:
    def minimumTotal(self, triangle:List[List[int]]):
        triangle = [t for i in triangle for t in i] 

        if len(triangle)==1:
            return triangle[0]
        else:
            counter=0
            dictToAdd={counter:[[0],triangle[0]]}
            pointerSum=deque([dictToAdd])
            LL=0
            val1=0
            val2=1
            while pointerSum:
                val1+=1
                val2+=1
                size=len(pointerSum)
                while size:
                    dictVal=pointerSum.popleft()
                    sumVal=list(dictVal.values())[0][1]
                    PL=list(dictVal.values())[0][0][-1]
                    if PL>LL:
                        diff=PL-LL
                        val1+=diff
                        val2+=diff
                    elif PL<LL:
                        diff=LL-PL
                        val1-=diff
                        val2-=diff
                    counter+=1
                    toAppend1={counter:[list(dictVal.values())[0][0]+[val1],sumVal+triangle[val1]]}
                    counter+=1
                    toAppend2={counter:[list(dictVal.values())[0][0]+[val2],sumVal+triangle[val2]]}
                    pointerSum.append(toAppend1)
                    pointerSum.append(toAppend2)
                    if val2==len(triangle)-1:
                        return min([list(v.values())[0][1] for v in pointerSum])
                    LL=PL
                    size-=1
            
            
